class HabitListView extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Consumer<HabitProvider>(
      builder: (context, habitProvider, _) {
        final habits = habitProvider.habits;

        return ListView.builder(
          itemCount: habits.length + 1, // +1 for Add New row
          itemBuilder: (context, index) {
            if (index == habits.length) {
              return AddNewHabitRow();
            }
            return HabitRow(habit: habits[index]);
          },
        );
      },
    );
  }
}

class HabitRow extends StatelessWidget {
  final Habit habit;

  const HabitRow({required this.habit});

  @override
  Widget build(BuildContext context) {
    return Dismissible(
      key: Key(habit.id),
      background: SwipeRightBackground(),
      secondaryBackground: SwipeLeftBackground(),
      onDismissed: (direction) {
        if (direction == DismissDirection.startToEnd) {
          // Complete/increment habit
        } else {
          // Show notes or undo
        }
      },
      child: ListTile(
        leading: CircleAvatar(child: Text(habit.icon)),
        title: Text(habit.name),
        subtitle: WeekStripIndicator(habitId: habit.id),
        trailing: StreakIndicator(streak: habit.currentStreak),
        onTap: () => _showHabitDetails(context, habit),
      ),
    );
  }
}

class SegmentedTimeline extends StatelessWidget {
  final Habit habit;
  final int currentValue;

  const SegmentedTimeline({
    required this.habit,
    required this.currentValue,
  });

  @override
  Widget build(BuildContext context) {
    return Row(
      children: List.generate(
        habit.targetValue,
        (index) => GestureDetector(
          onTap: () => _updateSegment(index),
          child: AnimatedContainer(
            duration: Duration(milliseconds: 200),
            margin: EdgeInsets.symmetric(horizontal: 2),
            width: 20,
            height: 20,
            decoration: BoxDecoration(
              color: _getSegmentColor(index),
              borderRadius: BorderRadius.circular(4),
            ),
          ),
        ),
      ),
    );
  }

  Color _getSegmentColor(int index) {
    if (index < currentValue) {
      return Theme.of(context).brightness == Brightness.light
          ? ChainyColors.lightSuccessGreen
          : ChainyColors.darkSuccessGreen;
    } else {
      return Theme.of(context).brightness == Brightness.light
          ? ChainyColors.lightInactiveGray
          : ChainyColors.darkInactiveGray;
    }
  }

  void _updateSegment(int index) {
    // Update segment logic with haptic feedback
    HapticFeedback.lightImpact();
    // Animation logic
  }
}
