Example implementation:
```dart
class HabitFormBottomSheet extends StatefulWidget {
  final Habit? habit; // Null for new habit, non-null for editing

  const HabitFormBottomSheet({this.habit});

  @override
  _HabitFormBottomSheetState createState() => _HabitFormBottomSheetState();
}

class _HabitFormBottomSheetState extends State<HabitFormBottomSheet> {
  final _formKey = GlobalKey<FormState>();
  late TextEditingController _nameController;
  late TextEditingController _noteController;
  String _selectedIcon = 'üìù';
  Color _selectedColor = Colors.blue;
  GoalType _goalType = GoalType.binary;
  int _targetValue = 1;
  String _unit = '';
  RecurrenceType _recurrenceType = RecurrenceType.daily;
  late RecurrenceConfig _recurrenceConfig;

  @override
  void initState() {
    super.initState();
    _initializeFormValues();
  }

  void _initializeFormValues() {
    if (widget.habit != null) {
      // Initialize with existing habit values
      _nameController = TextEditingController(text: widget.habit!.name);
      _noteController = TextEditingController(text: widget.habit!.note ?? '');
      _selectedIcon = widget.habit!.icon;
      _selectedColor = widget.habit!.color;
      _goalType = widget.habit!.goalType;
      _targetValue = widget.habit!.targetValue;
      _unit = widget.habit!.unit ?? '';
      _recurrenceType = widget.habit!.recurrenceType;
      _recurrenceConfig = widget.habit!.recurrenceConfig;
    } else {
      // Initialize with default values
      _nameController = TextEditingController();
      _noteController = TextEditingController();
      _recurrenceConfig = RecurrenceConfig.daily();
    }
  }

  @override
  Widget build(BuildContext context) {
    return Container(
      height: MediaQuery.of(context).size.height * 0.9,
      decoration: BoxDecoration(
        color: Theme.of(context).scaffoldBackgroundColor,
        borderRadius: BorderRadius.vertical(top: Radius.circular(20)),
      ),
      child: Column(
        children: [
          // Handle bar
          Container(
            width: 40,
            height: 4,
            margin: EdgeInsets.symmetric(vertical: 12),
            decoration: BoxDecoration(
              color: Theme.of(context).dividerColor,
              borderRadius: BorderRadius.circular(2),
            ),
          ),

          // Header
          Padding(
            padding: EdgeInsets.symmetric(horizontal: 20, vertical: 8),
            child: Row(
              mainAxisAlignment: MainAxisAlignment.spaceBetween,
              children: [
                TextButton(
                  onPressed: () => Navigator.of(context).pop(),
                  child: Text('Cancel'),
                ),
                Text(
                  widget.habit == null ? 'New Habit' : 'Edit Habit',
                  style: Theme.of(context).textTheme.titleLarge,
                ),
                TextButton(
                  onPressed: _saveHabit,
                  child: Text('Save'),
                ),
              ],
            ),
          ),

          Divider(),

          // Form content
          Expanded(
            child: Form(
              key: _formKey,
              child: ListView(
                padding: EdgeInsets.all(20),
                children: [
                  // Name field
                  TextFormField(
                    controller: _nameController,
                    decoration: InputDecoration(labelText: 'Habit Name'),
                    validator: (value) {
                      if (value == null || value.isEmpty) {
                        return 'Please enter a name';
                      }
                      return null;
                    },
                  ),

                  SizedBox(height: 16),

                  // Icon selector
                  IconSelectorWidget(
                    selectedIcon: _selectedIcon,
                    onIconSelected: (icon) => setState(() => _selectedIcon = icon),
                  ),

                  SizedBox(height: 16),

                  // Color picker
                  ColorPickerWidget(
                    selectedColor: _selectedColor,
                    onColorSelected: (color) => setState(() => _selectedColor = color),
                  ),

                  SizedBox(height: 16),

                  // Goal type selector
                  GoalTypeSelectorWidget(
                    goalType: _goalType,
                    onGoalTypeChanged: (type) => setState(() => _goalType = type),
                  ),

                  // Target value and unit (for quantitative goals)
                  if (_goalType == GoalType.quantitative) ...[  
                    SizedBox(height: 16),
                    TargetValueWidget(
                      targetValue: _targetValue,
                      unit: _unit,
                      onTargetValueChanged: (value) => setState(() => _targetValue = value),
                      onUnitChanged: (value) => setState(() => _unit = value),
                    ),
                  ],

                  SizedBox(height: 16),

                  // Recurrence selector
                  RecurrenceSelectorWidget(
                    recurrenceType: _recurrenceType,
                    recurrenceConfig: _recurrenceConfig,
                    onRecurrenceTypeChanged: (type) => setState(() => _recurrenceType = type),
                    onRecurrenceConfigChanged: (config) => setState(() => _recurrenceConfig = config),
                  ),

                  SizedBox(height: 16),

                  // Note field
                  TextFormField(
                    controller: _noteController,
                    decoration: InputDecoration(labelText: 'Note (Optional)'),
                    maxLines: 3,
                  ),
                ],
              ),
            ),
          ),
        ],
      ),
    );
  }

  void _saveHabit() {
    if (_formKey.currentState!.validate()) {
      final habitProvider = Provider.of<HabitProvider>(context, listen: false);

      final habit = Habit(
        id: widget.habit?.id ?? UUID.v4().toString(),
        name: _nameController.text,
        icon: _selectedIcon,
        color: _selectedColor,
        goalType: _goalType,
        targetValue: _targetValue,
        unit: _unit.isEmpty ? null : _unit,
        recurrenceType: _recurrenceType,
        recurrenceConfig: _recurrenceConfig,
        note: _noteController.text.isEmpty ? null : _noteController.text,
        createdAt: widget.habit?.createdAt ?? DateTime.now(),
        updatedAt: DateTime.now(),
      );

      habitProvider.saveHabit(habit);
      Navigator.of(context).pop();
    }
  }
}

// Helper function to show the bottom sheet
void showHabitFormBottomSheet(BuildContext context, {Habit? habit}) {
  showModalBottomSheet(
    context: context,
    isScrollControlled: true,
    backgroundColor: Colors.transparent,
    builder: (context) => HabitFormBottomSheet(habit: habit),
  );
}
