Example implementation:
```dart
class AchievementsScreen extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(title: Text('Achievements')),
      body: Consumer<AchievementProvider>(
        builder: (context, achievementProvider, _) {
          return GridView.builder(
            padding: EdgeInsets.all(16),
            gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(
              crossAxisCount: 3,
              childAspectRatio: 0.8,
              crossAxisSpacing: 16,
              mainAxisSpacing: 16,
            ),
            itemCount: achievementProvider.allAchievements.length,
            itemBuilder: (context, index) {
              final achievement = achievementProvider.allAchievements[index];
              return BadgeCard(
                achievement: achievement,
                isUnlocked: achievementProvider.isUnlocked(achievement.id),
                progress: achievementProvider.getProgress(achievement.id),
              );
            },
          );
        },
      ),
    );
  }
}

class BadgeCard extends StatelessWidget {
  final Achievement achievement;
  final bool isUnlocked;
  final double progress;

  const BadgeCard({
    required this.achievement,
    required this.isUnlocked,
    required this.progress,
  });

  @override
  Widget build(BuildContext context) {
    return GestureDetector(
      onTap: () => _showBadgeDetails(context),
      child: Card(
        elevation: isUnlocked ? 4 : 1,
        shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(12)),
        child: Padding(
          padding: EdgeInsets.all(8),
          child: Column(
            mainAxisAlignment: MainAxisAlignment.center,
            children: [
              Stack(
                alignment: Alignment.center,
                children: [
                  Container(
                    width: 60,
                    height: 60,
                    decoration: BoxDecoration(
                      shape: BoxShape.circle,
                      color: isUnlocked
                          ? _getCategoryColor(achievement.category)
                          : Theme.of(context).brightness == Brightness.light
                              ? ChainyColors.lightInactiveGray
                              : ChainyColors.darkInactiveGray,
                      boxShadow: isUnlocked
                          ? [
                              BoxShadow(
                                color: _getCategoryColor(achievement.category).withOpacity(0.3),
                                blurRadius: 8,
