class ReminderService {
  final FlutterLocalNotificationsPlugin flutterLocalNotificationsPlugin;
  final HabitRepository habitRepository;

  ReminderService(this.flutterLocalNotificationsPlugin, this.habitRepository);

  Future<void> initialize() async {
    const AndroidInitializationSettings initializationSettingsAndroid =
        AndroidInitializationSettings('app_icon');

    final DarwinInitializationSettings initializationSettingsIOS =
        DarwinInitializationSettings(
      requestSoundPermission: false,
      requestBadgePermission: false,
      requestAlertPermission: false,
    );

    final InitializationSettings initializationSettings = InitializationSettings(
      android: initializationSettingsAndroid,
      iOS: initializationSettingsIOS,
    );

    await flutterLocalNotificationsPlugin.initialize(
      initializationSettings,
      onDidReceiveNotificationResponse: _onNotificationTapped,
    );
  }

  Future<void> scheduleReminder(Reminder reminder) async {
    final habit = await habitRepository.getHabitById(reminder.habitId);
    if (habit == null) return;

    final androidDetails = AndroidNotificationDetails(
      'habit_reminders',
      'Habit Reminders',
      channelDescription: 'Notifications for habit reminders',
      importance: Importance.high,
      priority: Priority.high,
      color: habit.color,
    );

    final iosDetails = DarwinNotificationDetails(
      presentAlert: true,
      presentBadge: true,
      presentSound: true,
    );

    final notificationDetails = NotificationDetails(
      android: androidDetails,
      iOS: iosDetails,
    );

    // Create unique ID for this reminder
    final notificationId = int.parse('${habit.id.hashCode}${reminder.id.hashCode}'.substring(0, 9));

    // Schedule notification
    await flutterLocalNotificationsPlugin.zonedSchedule(
      notificationId,
      habit.name,
      'Time to ${habit.name}!',
      _nextInstanceOfTime(reminder.time),
      notificationDetails,
      androidAllowWhileIdle: true,
      uiLocalNotificationDateInterpretation: UILocalNotificationDateInterpretation.absoluteTime,
      matchDateTimeComponents: DateTimeComponents.time,
      payload: jsonEncode({
        'habitId': habit.id,
        'reminderId': reminder.id,
      }),
    );
  }

  Future<void> cancelReminder(String habitId, String reminderId) async {
    final notificationId = int.parse('${habitId.hashCode}${reminderId.hashCode}'.substring(0, 9));
    await flutterLocalNotificationsPlugin.cancel(notificationId);
  }

  Future<void> snoozeReminder(String habitId, String reminderId, Duration duration) async {
    final habit = await habitRepository.getHabitById(habitId);
    if (habit == null) return;

    final androidDetails = AndroidNotificationDetails(
      'habit_reminders',
      'Habit Reminders',
      channelDescription: 'Notifications for habit reminders',
      importance: Importance.high,
      priority: Priority.high,
      color: habit.color,
    );

    final iosDetails = DarwinNotificationDetails(
      presentAlert: true,
      presentBadge: true,
      presentSound: true,
    );

    final notificationDetails = NotificationDetails(
      android: androidDetails,
      iOS: iosDetails,
    );

    // Create unique ID for this snoozed reminder
    final notificationId = int.parse('${habitId.hashCode}${reminderId.hashCode}'.substring(0, 9));

    // Schedule snoozed notification
    await flutterLocalNotificationsPlugin.zonedSchedule(
      notificationId,
      habit.name,
      'Time to ${habit.name}! (Snoozed)',
      tz.TZDateTime.now(tz.local).add(duration),
      notificationDetails,
      androidAllowWhileIdle: true,
      uiLocalNotificationDateInterpretation: UILocalNotificationDateInterpretation.absoluteTime,
      payload: jsonEncode({
        'habitId': habitId,
        'reminderId': reminderId,
        'snoozed': true,
      }),
    );
  }

  void _onNotificationTapped(NotificationResponse response) {
    if (response.payload == null) return;

    try {
      final payloadData = jsonDecode(response.payload!);
      final habitId = payloadData['habitId'];
      final reminderId = payloadData['reminderId'];
      final snoozed = payloadData['snoozed'] ?? false;

      // Handle notification tap
      // Navigate to habit details or show snooze options
    } catch (e) {
      print('Error parsing notification payload: $e');
    }
  }

  tz.TZDateTime _nextInstanceOfTime(TimeOfDay time) {
    final now = tz.TZDateTime.now(tz.local);
    var scheduledDate = tz.TZDateTime(
      tz.local,
      now.year,
      now.month,
      now.day,
      time.hour,
      time.minute,
    );

    if (scheduledDate.isBefore(now)) {
      scheduledDate = scheduledDate.add(Duration(days: 1));
    }

    return scheduledDate;
  }
}

