}

class _OnboardingFlowState extends State<OnboardingFlow> {
  final PageController _pageController = PageController();
  int _currentStep = 0;
  final int _totalSteps = 5;

  // User input data
  String _userName = "";
  String _habitName = "";
  String _selectedIcon = "";
  Color _selectedColor = Colors.blue;
  String _selectedFrequency = "Daily";
  bool _notificationsEnabled = false;

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: ChainyColors.darkBackground,
      body: SafeArea(
        child: Column(
          children: [
            // Progress indicator
            Padding(
              padding: EdgeInsets.symmetric(horizontal: 24, vertical: 16),
              child: ProgressIndicator(
                currentStep: _currentStep,
                totalSteps: _totalSteps,
              ),
            ),

            // Main content
            Expanded(
              child: PageView(
                controller: _pageController,
                physics: NeverScrollableScrollPhysics(),
                children: [
                  NameInputScreen(
                    onNameEntered: (name) {
                      setState(() {
                        _userName = name;
                      });
                    },
                  ),
                  HabitNameScreen(
                    onHabitNameEntered: (name) {
                      setState(() {
                        _habitName = name;
                      });
                    },
                  ),
                  IconColorSelectionScreen(
                    onSelectionComplete: (icon, color) {
                      setState(() {
                        _selectedIcon = icon;
                        _selectedColor = color;
                      });
                    },
                  ),
                  FrequencySelectionScreen(
                    onFrequencySelected: (frequency) {
                      setState(() {
                        _selectedFrequency = frequency;
                      });
                    },
                  ),
                  NotificationPermissionScreen(
                    onPermissionResult: (granted) {
                      setState(() {
                        _notificationsEnabled = granted;
                      });
                    },
                  ),
                ],
              ),
            ),

            // Bottom navigation buttons
            Padding(
              padding: EdgeInsets.all(24),
              child: PrimaryButton(
                label: _currentStep < _totalSteps - 1 ? 'Next' : 'Get Started',
                onPressed: () => _handleNavigation(),
              ),
            ),
          ],
        ),
      ),
    );
  }

  void _handleNavigation() {
    if (_currentStep < _totalSteps - 1) {
      setState(() {
        _currentStep++;
      });
      _pageController.animateToPage(
        _currentStep,
        duration: Duration(milliseconds: 300),
        curve: Curves.easeInOut,
      );
    } else {
      _completeOnboarding();
    }
  }

  void _completeOnboarding() async {
    // Create the user's first habit
    final habit = Habit(
      id: UUID.v4().toString(),
      name: _habitName,
      icon: _selectedIcon,
      color: _selectedColor,
      recurrenceType: _parseFrequency(_selectedFrequency),
      createdAt: DateTime.now(),
      updatedAt: DateTime.now(),
    );

    // Save the habit
    final habitProvider = Provider.of<HabitProvider>(context, listen: false);
    await habitProvider.saveHabit(habit);

    // Save user name and onboarding completion status
    final prefs = await SharedPreferences.getInstance();
    await prefs.setString('user_name', _userName);
    await prefs.setBool('onboarding_completed', true);

    // Navigate to main app
    Navigator.of(context).pushReplacement(
      MaterialPageRoute(builder: (context) => MainScreen()),
    );
  }

  RecurrenceType _parseFrequency(String frequency) {
    // Convert selected frequency to app's RecurrenceType
    switch (frequency) {
      case 'Daily':
        return RecurrenceType.daily;
      case 'Multiple times per day':
        return RecurrenceType.multipleDaily;
      case '3√ó per week':
        return RecurrenceType.weekly;
      // Add other cases as needed
      default:
        return RecurrenceType.daily;
    }
  }
}

