class AnimationUtils {
  static Widget popAndGlow({
    required Widget child,
    required bool animate,
    Color glowColor = Colors.white,
    Duration duration = const Duration(milliseconds: 200),
  }) {
    return TweenAnimationBuilder<double>(
      tween: Tween<double>(begin: 0.0, end: animate ? 1.0 : 0.0),
      duration: duration,
      curve: Curves.easeOutBack,
      builder: (context, value, _) {
        return Transform.scale(
          scale: 1.0 + (0.2 * value),
          child: Container(
            decoration: BoxDecoration(
              boxShadow: [
                if (value > 0)
                  BoxShadow(
                    color: glowColor.withOpacity(0.7 * value),
                    blurRadius: 10 * value,
                    spreadRadius: 2 * value,
                  ),
              ],
            ),
            child: child,
          ),
        );
      },
    );
  }

  static Widget confetti({
    required Widget child,
    required bool animate,
    Duration duration = const Duration(milliseconds: 1500),
  }) {
    return Stack(
      children: [
        child,
        if (animate)
          ConfettiWidget(
            confettiController: ConfettiController(duration: duration)..play(),
            blastDirection: -pi / 2, // straight up
            emissionFrequency: 0.05,
            numberOfParticles: 20,
            maxBlastForce: 5,
            minBlastForce: 2,
            gravity: 0.1,
          ),
      ],
    );
  }

  static Widget badgeUnlock({
    required Widget child,
    required bool animate,
    Duration pulseDuration = const Duration(milliseconds: 400),
    Duration glowDuration = const Duration(milliseconds: 600),
    Duration sparkleDelay = const Duration(milliseconds: 300),
    Duration sparkleDuration = const Duration(milliseconds: 800),
  }) {
    return StatefulBuilder(
      builder: (context, setState) {
        if (!animate) return child;

        return Stack(
          alignment: Alignment.center,
          children: [
            // Pulse animation
            TweenAnimationBuilder<double>(
              tween: Tween<double>(begin: 0.0, end: 1.0),
              duration: pulseDuration,
              curve: Curves.easeOut,
              builder: (context, value, _) {
                return Transform.scale(
                  scale: 1.0 + (0.2 * sin(value * pi)),
                  child: child,
                );
              },
            ),

            // Glow effect
            TweenAnimationBuilder<double>(
              tween: Tween<double>(begin: 0.0, end: 1.0),
              duration: glowDuration,
              curve: Curves.easeOut,
              builder: (context, value, _) {
                return Container(
                  decoration: BoxDecoration(
                    shape: BoxShape.circle,
                    boxShadow: [
                      BoxShadow(
                        color: Colors.amber.withOpacity(0.7 * (1 - value)),
                        blurRadius: 20 * (1 - value),
                        spreadRadius: 10 * (1 - value),
                      ),
                    ],
                  ),
                );
              },
            ),

            // Sparkle effect (delayed)
            FutureBuilder<void>(
              future: Future.delayed(sparkleDelay),
              builder: (context, snapshot) {
                if (snapshot.connectionState != ConnectionState.done) {
                  return SizedBox.shrink();
                }

                return SparkleAnimation(
                  duration: sparkleDuration,
                );
              },
            ),
          ],
        );
      },
    );
  }
}

