class _SparkleAnimationState extends State<SparkleAnimation> with SingleTickerProviderStateMixin {
  late AnimationController _controller;
  late List<Sparkle> _sparkles;

  @override
  void initState() {
    super.initState();
    _controller = AnimationController(vsync: this, duration: widget.duration);
    _generateSparkles();
    _controller.forward();
  }

  void _generateSparkles() {
    final random = Random();
    _sparkles = List.generate(
      12,
      (index) => Sparkle(
        position: Offset(
          -50 + random.nextDouble() * 100,
          -50 + random.nextDouble() * 100,
        ),
        color: [Colors.amber, Colors.yellow, Colors.white][random.nextInt(3)],
        size: 2 + random.nextDouble() * 6,
        angle: random.nextDouble() * 2 * pi,
      ),
    );
  }

  @override
  void dispose() {
    _controller.dispose();
    super.dispose();
  }

  @override
  Widget build(BuildContext context) {
    return AnimatedBuilder(
      animation: _controller,
      builder: (context, child) {
        return CustomPaint(
          size: Size(100, 100),
          painter: SparklePainter(
            sparkles: _sparkles,
            progress: _controller.value,
          ),
        );
      },
    );
  }
}

class Sparkle {
  final Offset position;
  final Color color;
  final double size;
  final double angle;

  Sparkle({
    required this.position,
    required this.color,
    required this.size,
    required this.angle,
  });
}

class SparklePainter extends CustomPainter {
  final List<Sparkle> sparkles;
  final double progress;

  SparklePainter({
    required this.sparkles,
    required this.progress,
  });

  @override
  void paint(Canvas canvas, Size size) {
    final center = Offset(size.width / 2, size.height / 2);

    for (var sparkle in sparkles) {
      final distance = sparkle.position.distance * progress * 2;
      final opacity = (1 - progress) * 0.8;

      final paint = Paint()
        ..color = sparkle.color.withOpacity(opacity)
        ..style = PaintingStyle.fill;

      final position = center + Offset(
        sparkle.position.dx * progress * 2,
        sparkle.position.dy * progress * 2,
      );

      canvas.save();
      canvas.translate(position.dx, position.dy);
      canvas.rotate(sparkle.angle + progress * 2 * pi);

      // Draw star shape
      final path = Path();
      final starSize = sparkle.size * (1 - progress * 0.5);

      for (var i = 0; i < 5; i++) {
        final angle = i * 2 * pi / 5;
        final point = Offset(cos(angle) * starSize, sin(angle) * starSize);

        if (i == 0) {
          path.moveTo(point.dx, point.dy);
        } else {
          path.lineTo(point.dx, point.dy);
        }

        final innerAngle = angle + pi / 5;
        final innerPoint = Offset(
          cos(innerAngle) * starSize * 0.4,
          sin(innerAngle) * starSize * 0.4,
        );

        path.lineTo(innerPoint.dx, innerPoint.dy);
      }

      path.close();
      canvas.drawPath(path, paint);
      canvas.restore();
    }
  }

  @override
  bool shouldRepaint(covariant SparklePainter oldDelegate) {
    return oldDelegate.progress != progress;
  }
}
